sql.script:
if(type=='birthay'){
	
	select * from ( select id,name,birthday,datediff(concat(DATE_FORMAT(NOW(), '%Y'),right(birthday,6)),curdate()) as daycount,imgPathSmall from  custom where userId=#{userId}) aa where daycount < 61 and daycount >=0 order by daycount
}else if(type=='healthy'){
	
	select id,name,age,imgPathSmall from custom where userId=#{userId} and age between 25 and 44 and sex = '女' order by age
}else if(type=='money'){
	
	select id,name,age,imgPathSmall from custom where userId=#{userId} and age between 40 and 54 order by age
}else if(type=='family'){
	
	select id,name,age,imgPathSmall from custom where userId=#{userId} and age between 30 and 39 order by age
}else if(type=='longVisit'){
	
	select * from (select aa.*,bb.id,bb.name,bb.imgPathSmall,datediff(curdate(),aa.visitTime) as daysnovisit from (select a.visitCustomId,max(a.visitTime) as visittime from usertrip a where userid=#{userId} and a.state in(2,3) group by a.visitCustomId) aa join custom bb on aa.visitCustomId = bb.id) aaa where aaa.daysnovisit > 90 order by aaa.daysnovisit desc
}else if(type=='custionData'){
	
	select * from custom where birthDay is null or birthDay ='' and userid = #{userId}
}else if(type=='plan'){
	
	
		select aaa.*,bbb.name from(
		select 
		case when usertripid is null then 'plan' else 'trip' end as idtype,
		case when usertripid is null then customid else visitcustomid end as customid
		 from(
		(select distinct a.id as usertripId,a.visitCustomId from usertrip a  where a.userid=#{userId} and a.state=1 and left(a.visittime,7)=left(now(),7)) aa 
		left join 
		(select id as planid ,customId from userplan  where userid=#{userId} and plandate =left(now(),7)) bb
		on aa.visitCustomId=bb.customId
		)
		UNION 
		select 
		case when usertripid is null then 'plan' else 'trip' end as idtype,
		case when usertripid is null then customid else visitcustomid end as customid
		 from(
		(select distinct a.id as usertripId,a.visitCustomId from usertrip a  where a.userid=#{userId} and a.state=1 and left(a.visittime,7)=left(now(),7)) aa 
		right join 
		(select id as planid ,customId from userplan  where userid=#{userId} and plandate =left(now(),7)) bb
		on aa.visitCustomId=bb.customId
		)
		) aaa join custom bbb on aaa.customid=bbb.id
	
}
