sqls.sql:
select * from custom where userId=#{userId}

	if(customType == '营销' || customType == '增员'){
		and customType=#{customType}
	}
	if(level != null){
		and level = #{level}
	}
	if(birthDay != null){
		and month(birthDay)=#{birthDay}
	}
	if(car != null){
		if(car == '有'){
			and carExpireDate is not null and carExpireDate != ''
		}else{
			and carExpireDate is null
		}
	}
	if(ageStart != null && ageEnd != null){
		and age >= #{ageStart} and age <= #{ageEnd}
	}
	if(whimStart != null && whimEnd != null){
		and whim >= #{whimStart} and whim <= #{whimEnd}
	}
	if(sex != null){
		and sex=#{sex}
	}
	if(hope != null){
		and hope=#{hope}
	}
	if(relationship != null){
		and relationship=#{relationship}
	}


	if(orderType != null){
		if(orderType == 'abc'){
			order by IF(ISNULL(abc),1,0)
		}else if(orderType == 'customlevel'){
			order by IF(ISNULL(level),1,0),abc
		}else if(orderType == 'birthday'){
			order by IF(ISNULL(month(birthDay)),1,0),abc
		}else if(orderType == 'visitLateTime'){
			order by IF(ISNULL(visitLastTime),1,0) desc,abc
		}else if(orderType == 'visitLongTime'){
			order by IF(ISNULL(visitLastTime),1,0),abc
		}else if(orderType == 'age'){
			order by IF(ISNULL(age),1,0),abc
		}else if(orderType == 'carExpireDate'){
			order by IF(ISNULL(carExpireDate),1,0),abc
		}else if(orderType == 'policy'){
			order by IF(ISNULL(policy),1,0) desc,abc
		}else if(orderType == 'yearIncome'){
			order by IF(ISNULL(yearIncome),1,0) desc,abc
		}
	}else{
		order by IF(ISNULL(abc),1,0)
	}
