sqls.sql:
select * from GroupNotice where 1=1 
	
		and groupId=#{groupId} 
	
	
		and id = #{id}
	
 order by id desc

select a.*,b.name as userName,b.imgUrl as userImgUrl from GroupJournal a join user b on a.userid=b.id 
where 1=1 

	and a.groupId=#{groupId}


	and a.id = #{journalId}


	and a.userid=#{userId}

 order by a.id desc
